import java.util.Scanner;public class Recursion {  public static   int fact(int number){        if (number==1 || number==0){            return 1;        }        else {            return number*fact(number-1);        }    }    static int a=0,b=1,c=0;  public static void fibinacchi(int count){      if(count>0){          c=a+b;          a=b;          b=c;          System.out.print(c+" ");          fibinacchi(count-1);      }  }    public static void main(String[] args) {      int count;        Scanner input=new Scanner(System.in);        count=input.nextInt();        System.out.println(fact(count));        System.out.print(a+" "+b+" ");        fibinacchi(15-2);    }}