import java.util.ArrayList;import java.util.Arrays;public class SolutionOf_ArrayProblem { static void reverse(int[] ar){        int start=0;        int end=ar.length-1;        while (end>start){            swap(ar,start,end);            start++;            end--;        }    }static void swap(int[]arr,int index1,int index2){        int temp=arr[index1];        arr[index1]=arr[index2];        arr[index2]=temp;}    public static void main(String[] args) {        int [] ar= {1,2,3,4,5};        reverse(ar);        for (int result:ar             ) {            System.out.print(result);        }    }}